import * as React from 'react';
import { useI18n, useMFA } from '@/hooks';
import type { ManageMfaProps, MFAType, Authenticator } from '@/types';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Toaster } from '@/components/ui/sonner';
import { EnrollmentForm } from '@/components/mfa/enrollment-form';

/**
 * ManageMfa Component
 *
 * A component responsible for managing Multi-Factor Authentication (MFA) factors for a user.
 * This component handles the mfa access token fetching ,fetching authenticators, enrolling, and deletion of MFA factors and manages the MFA access token.
 * It operates in both ProxyMode (RWA) and SPA modes for authentication.
 *
 * - **ProxyMode (RWA)**: In this mode, the component interacts with a proxy service to manage MFA
 * - **SPA (Single Page Application)**: In this mode, the component communicates directly with the API to manage MFA factors.
 *
 * @param {Object} props - The properties passed to the component.
 * @param {Object} [props.localization={}] - Localization object for i18n support.
 * This object should contain key-value pairs for each language and its associated translations.
 *
 * Example:
 * ```js
 * localization={{
 *   title: 'Manage MFA Factors',
 *   description: 'Here you can manage your Multi-Factor Authentication (MFA) factors.',
 *   loading: 'Loading...',
 *   errors: {
 *     factorsLoadingError: 'An error occurred while loading MFA factors.',
 *   },
 *   no_active_mfa: 'No active MFA factors found.',
 *   enroll_factor: 'Successfully enrolled the MFA factor.',
 *   remove_factor: 'Successfully removed the MFA factor.',
 *   delete: 'Delete',
 *   enroll: 'Enroll',
 * }}
 * ```
 * @param {boolean} [props.hideHeader=false] - Whether to hide the header.
 * @param {boolean} [props.showActiveOnly=false] - Whether to show only active MFA factors.
 * @param {boolean} [props.disableEnroll=false] - Whether to disable the enrollment of new factors.
 * @param {boolean} [props.disableDelete=false] - Whether to disable the deletion of factors.
 * @param {boolean} [props.readOnly=false] - Whether the component is in read-only mode.
 * @param {Object} [props.factorConfig={}] - Configuration for MFA factors, controlling visibility and enabled state.
 * @param {Function} [props.onEnroll] - Callback fired when an MFA factor is successfully enrolled.
 * @param {Function} [props.onDelete] - Callback fired when an MFA factor is successfully deleted.
 * @param {Function} [props.onFetch] - Callback fired when MFA factors are fetched.
 * @param {Function} [props.onErrorAction] - Callback fired when an error occurs during an action (enroll/delete).
 * @param {Function} [props.onBeforeAction] - Callback fired before performing an action (enroll/delete).
 *
 * @returns {React.JSX.Element} The rendered component.
 */
export function ManageMfa({
  localization = {},
  hideHeader = false,
  showActiveOnly = false,
  disableEnroll = false,
  disableDelete = false,
  readOnly = false,
  factorConfig = {},
  onEnroll,
  onDelete,
  onFetch,
  onErrorAction,
  onBeforeAction,
}: ManageMfaProps): React.JSX.Element {
  const t = useI18n('mfa', localization);
  const { fetchFactors, enrollMfa, deleteMfa, confirmEnrollment } = useMFA();

  const [factors, setFactors] = React.useState<Authenticator[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [deleting, setDeleting] = React.useState(false);
  const [dialogOpen, setDialogOpen] = React.useState(false);
  const [enrollFactor, setEnrollFactor] = React.useState<MFAType | null>(null);

  /**
   * Loads the available MFA factors from the API and updates the state.
   * This is called on initial load and when factors need to be refreshed.
   */
  const loadFactors = React.useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const factors = await fetchFactors(showActiveOnly);
      setFactors(factors);
      onFetch?.();
    } catch (err) {
      setError(t('errors.factors_loading_error'));
    }

    setLoading(false);
  }, [fetchFactors, showActiveOnly, onFetch, onErrorAction]);

  React.useEffect(() => {
    loadFactors();
  }, [loadFactors]);

  /**
   * Filters visible MFA factors based on the provided factor configuration.
   * Each factor's visibility is determined by the configuration settings.
   *
   * @returns {Authenticator[]} Filtered MFA factors.
   */
  const visibleFactors = React.useMemo(() => {
    return factors.filter((factor) => {
      const config = factorConfig[factor.factorName as keyof typeof factorConfig];
      return config?.visible !== false;
    });
  }, [factors, factorConfig]);

  /**
   * Handles the enrollment button click for a specific MFA factor.
   * Opens the enrollment dialog for the chosen factor.
   *
   * @param {MFAType} factor - The MFA factor to be enrolled.
   */
  const handleEnrollClick = (factor: MFAType) => {
    setEnrollFactor(factor);
    setDialogOpen(true);
  };

  /**
   * Handles the deletion of an MFA factor.
   * If the action is allowed (i.e., not read-only or disabled), it calls the deleteMfa function.
   *
   * @param {string} factorId - The ID of the factor to be deleted.
   * @param {MFAType} factorType - The type of the MFA factor to be deleted.
   */
  const handleDelete = React.useCallback(
    async (factorId: string, factorType: MFAType) => {
      if (readOnly || disableDelete) return;

      if (onBeforeAction) {
        const proceed = await onBeforeAction('delete', factorType);
        if (!proceed) return;
      }

      setDeleting(true);

      try {
        await deleteMfa(factorId);
        toast.success(t('remove_factor'), {
          duration: 2000,
          onAutoClose: async () => {
            onDelete?.();
            await loadFactors();
          },
        });
      } catch (err) {
        const e = err as Error;
        toast.error(e.message);
        onErrorAction?.(e, 'delete');
      } finally {
        setDeleting(false);
      }
    },
    [readOnly, disableDelete, onBeforeAction, deleteMfa, onErrorAction, onDelete, loadFactors, t],
  );

  /**
   * Handles the successful enrollment of an MFA factor.
   * Displays a success message and reloads the factors list.
   */
  const handleEnrollSuccess = React.useCallback(async () => {
    setDialogOpen(false);
    setEnrollFactor(null);
    toast.success(t('enroll_factor'), {
      duration: 2000,
      onAutoClose: async () => {
        onEnroll?.();
        await loadFactors();
      },
    });
  }, [loadFactors, onEnroll]);

  /**
   * Handles errors during the enrollment or confirmation process.
   *
   * @param {Error} error - The error object containing the failure message.
   * @param {string} stage - The stage of the process ('enroll' or 'confirm').
   */
  const handleEnrollError = React.useCallback(
    (error: Error, stage: 'enroll' | 'confirm') => {
      toast.error(`${stage === 'enroll' ? 'Enrollment' : 'Confirmation'} failed: ${error.message}`);
      onErrorAction?.(error, stage);
    },
    [onErrorAction],
  );

  if (loading) return <p>{t('loading')}</p>;
  if (error) return <p className="text-3xl font-bold underline">{error}</p>;

  return (
    <>
      <Toaster position="top-right" />
      <Card>
        {!hideHeader && (
          <CardHeader>
            <CardTitle>{t('title')}</CardTitle>
            <CardDescription>{t('description')}</CardDescription>
          </CardHeader>
        )}

        <CardContent className="grid gap-6 p-4 pt-0 md:p-6 md:pt-0">
          {showActiveOnly && visibleFactors.length === 0 ? (
            <Label className="text-center text-muted-foreground">{t('no_active_mfa')}</Label>
          ) : (
            visibleFactors.map((factor, idx) => {
              const isEnabledFactor =
                factorConfig?.[factor.factorName as MFAType]?.enabled !== false;

              return (
                <div
                  key={`${factor.name}-${idx}`}
                  className={`flex flex-col gap-6 ${!isEnabledFactor ? 'opacity-50 pointer-events-none' : ''}`}
                  aria-disabled={!isEnabledFactor}
                >
                  {idx > 0 && <Separator />}
                  <div className="flex flex-col items-center justify-between space-y-6 md:flex-row md:space-x-2 md:space-y-0">
                    <Label className="flex flex-col items-start space-y-1">
                      <span className="leading-6 text-left">
                        {t(`${factor.factorName}.title`)}
                        {factor.active && (
                          <Badge variant="default" color="green" className="ml-3">
                            {t('enrolled')}
                          </Badge>
                        )}
                      </span>
                      <p className="font-normal leading-snug text-muted-foreground text-left">
                        {t(`${factor.factorName}.description`)}
                      </p>
                    </Label>

                    <div className="flex items-center justify-end space-x-24 md:min-w-72">
                      {factor.active
                        ? !readOnly && (
                            <Button
                              type="submit"
                              onClick={() => handleDelete(factor.id, factor.factorName as MFAType)}
                              disabled={disableDelete || deleting || !isEnabledFactor}
                              aria-label={`Delete authenticator ${factor.factorName}`}
                            >
                              {t('delete')}
                            </Button>
                          )
                        : !readOnly && (
                            <Button
                              onClick={() => handleEnrollClick(factor.factorName as MFAType)}
                              disabled={disableEnroll || !isEnabledFactor}
                            >
                              {t('enroll')}
                            </Button>
                          )}
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </CardContent>
      </Card>

      {enrollFactor && (
        <EnrollmentForm
          open={dialogOpen}
          onClose={() => {
            setDialogOpen(false);
            setEnrollFactor(null);
          }}
          factorType={enrollFactor}
          enrollMfa={enrollMfa}
          confirmEnrollment={confirmEnrollment}
          onSuccess={handleEnrollSuccess}
          onError={handleEnrollError}
        />
      )}
    </>
  );
}

import * as React from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

import { MFAType, normalizeError } from '@auth0-web-ui-components/core';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormControl,
  FormMessage,
} from '@/components/ui/form';
import { InputOTP, InputOTPGroup, InputOTPSlot } from '@/components/ui/input-otp';
import { Input } from '@/components/ui/input';
import { useI18n } from '@/hooks';
import { EnrollMfaResponse } from '@auth0-web-ui-components/core';
import QRCode from 'react-qr-code';

const phoneRegex = /^\+?[0-9\s\-()]{8,}$/;

type EnrollmentFormProps = {
  open: boolean;
  onClose: () => void;
  factorType: MFAType;
  enrollMfa: (factor: MFAType, options: Record<string, string>) => Promise<EnrollMfaResponse>;
  confirmEnrollment: (
    factor: MFAType,
    options: { oobCode?: string; userOtpCode?: string; userEmailOtpCode?: string },
  ) => Promise<unknown | null>;
  onSuccess: () => void;
  onError: (error: Error, stage: 'enroll' | 'confirm') => void;
};

type ContactForm = {
  contact: string;
};

type OtpForm = {
  userOtp: string;
};

export function EnrollmentForm({
  open,
  onClose,
  factorType,
  enrollMfa,
  confirmEnrollment,
  onSuccess,
  onError,
}: EnrollmentFormProps) {
  const t = useI18n('mfa');

  // Initialize phase as null, meaning no UI shown by default
  const [phase, setPhase] = React.useState<'enterContact' | 'enterOtp' | 'showOtp' | null>(null);
  const [oobCode, setOobCode] = React.useState<string | undefined>(undefined);
  const [otpData, setOtpData] = React.useState<{
    secret: string | null;
    barcodeUri: string | null;
    recoveryCodes: string[];
  }>({
    secret: null,
    barcodeUri: null,
    recoveryCodes: [],
  });
  const [loading, setLoading] = React.useState(false);

  // Create schema for validating contact input based on MFA type
  const ContactSchema = React.useMemo(() => {
    return factorType === 'email'
      ? z.object({ contact: z.string().email({ message: 'Invalid email address' }) })
      : z.object({ contact: z.string().regex(phoneRegex, { message: 'Invalid phone number' }) });
  }, [factorType]);

  const formContact = useForm<ContactForm>({
    resolver: zodResolver(ContactSchema),
    mode: 'onChange',
  });

  const formOtp = useForm<OtpForm>({
    mode: 'onChange',
  });

  React.useEffect(() => {
    if (!open) {
      setPhase(null); // reset phase to null when dialog closes
      setOobCode(undefined);
      setOtpData({ secret: null, barcodeUri: null, recoveryCodes: [] });
      setLoading(false);
      formContact.reset();
      formOtp.reset();
    }
  }, [open, formContact, formOtp]);

  React.useEffect(() => {
    if (open && (factorType === 'email' || factorType === 'sms')) {
      setPhase('enterContact');
    }
  }, [open, factorType]);

  const onSubmitContact = async (data: ContactForm) => {
    setLoading(true);
    try {
      const options: Record<string, string> =
        factorType === 'email'
          ? { email: data.contact }
          : factorType === 'sms'
            ? { phone_number: data.contact }
            : {};

      const response = await enrollMfa(factorType, options);

      if (response?.oob_code) {
        setOobCode(response.oob_code);
        setPhase('enterOtp');
      }

      if (response?.authenticator_type === 'otp') {
        setOtpData({
          secret: response.secret ?? null,
          barcodeUri: response.barcode_uri ?? null,
          recoveryCodes: response.recovery_codes || [],
        });
        setPhase('showOtp');
      }
    } catch (error) {
      const normalizedError = normalizeError(error, {
        resolver: (code) => t(`errors.${code}.${factorType}`),
        fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
      });
      onError(normalizedError, 'enroll');
    } finally {
      setLoading(false);
    }
  };

  const onSubmitOtp = async (data: OtpForm) => {
    setLoading(true);

    try {
      const options = {
        oobCode,
        ...(factorType === 'email'
          ? { userEmailOtpCode: data.userOtp }
          : { userOtpCode: data.userOtp }),
      };

      const response = await confirmEnrollment(factorType, options);
      if (response) {
        onSuccess();
        onClose();
      }
    } catch (err) {
      const normalizedError = normalizeError(err, {
        resolver: (code) => t(`errors.${code}.${factorType}`),
        fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
      });
      onError(normalizedError, 'confirm');
    } finally {
      setLoading(false);
    }
  };

  // Automatically initiate OTP enrollment when factorType is 'totp' or 'push-notification'
  React.useEffect(() => {
    if (['totp', 'push-notification'].includes(factorType) && !otpData.secret && open) {
      setLoading(true);
      const fetchOtpEnrollment = async () => {
        try {
          const response = await enrollMfa(factorType, {});
          if (response?.authenticator_type === 'otp') {
            setOtpData({
              secret: response.secret ?? null,
              barcodeUri: response.barcode_uri ?? null,
              recoveryCodes: response.recovery_codes || [],
            });
          }
          setPhase('showOtp');
        } catch (error) {
          const normalizedError = normalizeError(error, {
            resolver: (code) => t(`errors.${code}.${factorType}`),
            fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
          });
          onError(normalizedError, 'enroll');
          onClose();
        } finally {
          setLoading(false);
        }
      };

      fetchOtpEnrollment();
    }
  }, [factorType, enrollMfa, onError, otpData.secret, open]);

  // Render the appropriate form based on the current phase and factorType
  const renderForm = () => {
    switch (phase) {
      case 'enterContact':
        return (
          <div className="w-full max-w-sm mx-auto text-center">
            <Form {...formContact}>
              <form onSubmit={formContact.handleSubmit(onSubmitContact)} className="space-y-6">
                <FormField
                  control={formContact.control}
                  name="contact"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>
                        {factorType === 'email' ? 'Email Address' : 'Phone Number (SMS)'}
                      </FormLabel>
                      <FormControl>
                        <Input
                          placeholder={factorType === 'email' ? 'your@email.com' : '+1234567890'}
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button type="submit" disabled={!formContact.formState.isValid || loading}>
                  {loading ? 'Sending...' : 'Send Code'}
                </Button>
              </form>
            </Form>
          </div>
        );
      case 'showOtp':
        return (
          <div className="text-center">
            <p>Scan the barcode with your authenticator app</p>
            <div className="flex justify-center items-center mt-6">
              <div className="border border-gray-300 p-4 rounded-lg shadow-lg bg-white inline-block">
                <QRCode
                  size={150}
                  style={{ height: 'auto', maxWidth: '100%', width: '100%' }}
                  value={otpData.barcodeUri || ''}
                  viewBox={`0 0 150 150`}
                />
              </div>
            </div>
            <div className="mt-6">
              {otpData.recoveryCodes.length > 0 && (
                <div className="mb-6">
                  <p>
                    <strong>Save these recovery codes!</strong>
                  </p>
                  <ul className="list-none inline-block bg-gray-100 dark:bg-gray-800 p-4 rounded-lg mt-2">
                    {otpData.recoveryCodes.map((code, index) => (
                      <li key={index} className="font-mono tracking-widest">
                        {code}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
              <div className="w-full max-w-sm mx-auto text-center">
                <Form {...formOtp}>
                  <form
                    autoComplete="off"
                    onSubmit={formOtp.handleSubmit(onSubmitOtp)}
                    className="space-y-6 mt-4"
                  >
                    <FormField
                      control={formOtp.control}
                      name="userOtp"
                      key={phase}
                      render={({ field }) => (
                        <FormItem className="text-center">
                          <FormLabel className="block w-full text-sm font-medium text-center">
                            Enter the code from your authenticator app
                          </FormLabel>
                          <FormControl>
                            <div className="flex justify-center">
                              <InputOTP maxLength={6} {...field} autoComplete="one-time-code">
                                <InputOTPGroup>
                                  <InputOTPSlot index={0} />
                                  <InputOTPSlot index={1} />
                                  <InputOTPSlot index={2} />
                                  <InputOTPSlot index={3} />
                                  <InputOTPSlot index={4} />
                                  <InputOTPSlot index={5} />
                                </InputOTPGroup>
                              </InputOTP>
                            </div>
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <Button type="submit" disabled={loading}>
                      {loading ? 'Verifying...' : 'Verify Code'}
                    </Button>
                  </form>
                </Form>
              </div>
            </div>
          </div>
        );
      case 'enterOtp':
        return (
          <div className="w-full max-w-sm mx-auto text-center">
            <Form {...formOtp}>
              <form
                onSubmit={formOtp.handleSubmit(onSubmitOtp)}
                autoComplete="off"
                className="space-y-6"
              >
                <FormField
                  control={formOtp.control}
                  key={phase}
                  name="userOtp"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Enter the verification code</FormLabel>
                      <FormControl>
                        <InputOTP maxLength={6} {...field} autoComplete="one-time-code">
                          <InputOTPGroup>
                            <InputOTPSlot index={0} />
                            <InputOTPSlot index={1} />
                            <InputOTPSlot index={2} />
                            <InputOTPSlot index={3} />
                            <InputOTPSlot index={4} />
                            <InputOTPSlot index={5} />
                          </InputOTPGroup>
                        </InputOTP>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button type="submit" disabled={loading}>
                  {loading ? 'Verifying...' : 'Verify Code'}
                </Button>
              </form>
            </Form>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <Dialog open={open && Boolean(phase)} onOpenChange={onClose}>
      <DialogContent aria-describedby={factorType}>
        <DialogHeader>
          <DialogTitle className="text-center">
            {factorType === 'email'
              ? 'Enroll Email MFA'
              : factorType === 'sms'
                ? 'Enroll SMS MFA'
                : t('enroll_otp_mfa')}
          </DialogTitle>
        </DialogHeader>
        {renderForm()}
      </DialogContent>
    </Dialog>
  );
}

import * as React from 'react';
import { cva, VariantProps } from 'class-variance-authority';

import { cn } from '@/lib/utils';

const badgeVariants = cva(
  'inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
  {
    variants: {
      variant: {
        default: 'border-transparent bg-primary text-primary-foreground shadow hover:bg-primary/80',
        secondary:
          'border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80',
        destructive:
          'border-transparent bg-destructive text-destructive-foreground shadow hover:bg-destructive/80',
        outline: 'text-foreground',
      },
    },
    defaultVariants: {
      variant: 'default',
    },
  },
);

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return <div className={cn(badgeVariants({ variant }), className)} {...props} />;
}

export { Badge, badgeVariants };

import * as React from 'react';
import { Slot } from '@radix-ui/react-slot';
import { cva, type VariantProps } from 'class-variance-authority';

import { cn } from '@/lib/utils';

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground shadow-xs hover:bg-primary/90',
        destructive:
          'bg-destructive text-white shadow-xs hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60',
        outline:
          'border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50',
        secondary: 'bg-secondary text-secondary-foreground shadow-xs hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50',
        link: 'text-primary underline-offset-4 hover:underline',
      },
      size: {
        default: 'h-9 px-4 py-2 has-[>svg]:px-3',
        sm: 'h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5',
        lg: 'h-10 rounded-md px-6 has-[>svg]:px-4',
        icon: 'size-9',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  },
);

function Button({
  className,
  variant,
  size,
  asChild = false,
  ...props
}: React.ComponentProps<'button'> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean;
  }) {
  const Comp = asChild ? Slot : 'button';

  return (
    <Comp
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  );
}

export { Button, buttonVariants };

import * as React from 'react';

import { cn } from '@/lib/utils';

function Card({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="card"
      className={cn(
