import * as React from 'react';
import { mergeThemes } from '@/lib/utils';
import type { BrandingTheme, CustomOverrides, ThemeContextValue, ThemeInput } from './types';

/**
 * Default branding theme if none is provided.
 */
const defaultBranding: BrandingTheme = {
  mode: 'light',
};

/**
 * Default empty customer overrides.
 */
const defaultCustomOverrides: CustomOverrides = {};

/**
 * ThemeContext
 *
 * Provides access to branding theme, customer overrides,
 * and a merged theme object for convenience.
 */
export const ThemeContext = React.createContext<ThemeContextValue>({
  branding: defaultBranding,
  customOverrides: defaultCustomOverrides,
  mergedTheme: mergeThemes(defaultBranding, defaultCustomOverrides),
});

/**
 * ThemeProvider
 *
 * Provides theme configuration via React Context to all components in the tree.
 * It merges a branding theme (from UL) and optional customer overrides (CSS variables).
 *
 * @param theme - Optional branding and customerOverrides
 * @param children - The components that will have access to the theme
 *
 * @example
 * ```tsx
 * <ThemeProvider
 *   theme={{
 *     branding: { mode: 'dark', primaryColor: '#0070f3' },
 *     customerOverrides: { '--font-size': '14px' }
 *   }}
 * >
 *   <App />
 * </ThemeProvider>
 * ```
 */
export const ThemeProvider: React.FC<{
  theme?: ThemeInput;
  children: React.ReactNode;
}> = ({ theme, children }) => {
  const branding = React.useMemo(() => theme?.branding ?? defaultBranding, [theme?.branding]);
  const customOverrides = React.useMemo(
    () => theme?.customOverrides ?? defaultCustomOverrides,
    [theme?.customOverrides],
  );

  const mergedTheme = React.useMemo(
    () => mergeThemes(branding, customOverrides),
    [branding, customOverrides],
  );

  return (
    <ThemeContext.Provider value={{ branding, customOverrides, mergedTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

import type {
  EnrollMfaResponse,
  AuthenticatorType,
  OobChannel,
} from '@auth0-web-ui-components/core';

/**
 * Represents the type of an MFA authenticator.
 *
 * Common values include:
 * - 'sms' — Phone Message: Users receive a phone message with a verification code.
 * - 'push-notification' — Push Notification using Auth0 Guardian: Provides push notifications for authentication.
 * - 'otp' — One-time Password: Uses apps like Google Authenticator for OTP codes.
 * - 'email' — Email: Sends a verification code via email.
 * - 'duo' — Duo Security: Uses Duo Security for MFA.
 * - 'webauthn-roaming' — WebAuthn with FIDO Security Keys: Supports external security keys (e.g., FIDO2).
 * - 'webauthn-platform' — WebAuthn with FIDO Device Biometrics: Uses device biometrics compliant with WebAuthn.
 * - 'recovery-code' — Recovery Code: Uses unique recovery codes to regain access.
 *
 * This type can also be extended with custom string values for other authenticators.
 */
export type MFAType =
  | 'sms'
  | 'push-notification'
  | 'totp'
  | 'email'
  | 'duo'
  | 'webauthn-roaming'
  | 'webauthn-platform'
  | 'recovery-code';

/**
 * Represents an MFA authenticator linked to a user.
 */
export interface MFAFactor {
  id: string;
  authenticator_type: AuthenticatorType;
  oob_channels?: OobChannel[];
  name?: string;
  active: boolean;
}

/**
 * Error object for MFA operations.
 * Extends the standard Error with optional code and additional details.
 */
