/**
 * Auth0ComponentContext
 *
 * React Context that provides configuration and authentication state
 * related to Auth0 integration throughout the component tree.
 *
 * The context value includes:
 * - `config`: An object containing:
 *    - `i18n`: Internationalization settings (current and fallback languages).
 *    - `themeSettings`: Theme and branding configurations.
 *    - `customOverrides`: Optional CSS variable overrides.
 *    - `isProxyMode`: Boolean indicating whether proxy mode is active.
 *    - `apiBaseUrl`: Base URL used for API calls (proxy URL or Auth0 domain).
 *    - `authDetails` (optional): Authentication details such as access tokens,
 *      domain, client ID, scopes, loading state, and any errors. Present only in SPA mode.
 *
 * This context is intended to be consumed by any component needing access to Auth0 configuration,
 * theming, internationalization, or authentication status.
 */
export const Auth0ComponentContext = React.createContext<{
  config: Auth0ComponentConfig & {
    authDetails?: AuthDetails;
    isProxyMode: boolean;
    apiBaseUrl: string;
  };
}>({
  config: {
    i18n: { currentLanguage: 'en-US', fallbackLanguage: 'en-US' },
    themeSettings: { mode: 'light' },
    customOverrides: {},
    isProxyMode: false,
    apiBaseUrl: '',
  },
});

import * as React from 'react';
/**
 * Theme configuration for the Auth0 components
 * @property {('light'|'dark')} [mode] - Theme mode
 * @property {string} [primaryColor] - Primary color for theming
 */
export interface ThemeSettings {
  mode?: 'light' | 'dark';
  primaryColor?: string;
  [key: string]: unknown;
}

/**
 * Configuration options for Auth0ComponentProvider
 *
 * Defines configuration for authentication, internationalization, and theming.
 * Also optionally includes runtime authentication state in SPA mode.
 *
 * @property {string} [authProxyUrl] - Optional URL for authentication proxy in RWA (proxy) mode.
 *                                      If provided, authentication is handled externally and no tokens are fetched.
 *
 * @property {I18nOptions} [i18n] - Internationalization settings including current and fallback languages.
 *
 *
 * @property {ThemeSettings} [themeSettings] - UI theme configuration, such as light/dark mode and primary color.
 *
 * @property {AuthDetails} [authDetails] - Runtime authentication state, populated in SPA (non-proxy) mode.
 *                                         Includes access token, domain, client ID, scopes, loading, and error info.
 */
export interface Auth0ComponentConfig {
  authProxyUrl?: string;
  i18n?: I18nOptions;
  themeSettings?: ThemeSettings;
  authDetails?: AuthDetails;
  customOverrides?: CustomOverrides;
  isProxyMode?: boolean;
  apiBaseUrl?: string;
  loader?: React.ReactNode;
}

export interface I18nOptions {
  currentLanguage: string;
  fallbackLanguage?: string;
}

/**
 * Auth0 authentication details fetched from SDK
 */
export interface AuthDetails {
  accessToken: string | undefined;
  domain: string | undefined;
  clientId: string | undefined;
  scopes: string | undefined;
  loading: boolean;
  error?: Error;
}

/**
 * BrandingTheme
 *
 * Controlled UL branding configuration.
 */
export type BrandingTheme = {
  mode?: 'light' | 'dark' | 'system';
  primaryColor?: string;
  borderRadius?: number;
  fontFamily?: string;
  [key: string]: unknown;
};

/**
 * CustomerOverrides
 *
 * Custom CSS variable overrides (e.g. "--button-radius": "6px").
 */
export type CustomOverrides = Record<string, string>;

/**
 * ThemeInput
 *
 * Optional props passed into the ThemeProvider.
 */
export type ThemeInput = {
  branding?: BrandingTheme;
  customOverrides?: CustomOverrides;
};

/**
 * ThemeContextValue
 *
 * The values made available through the ThemeContext.
 */
export type ThemeContextValue = {
  branding: BrandingTheme;
  customOverrides: CustomOverrides;
  mergedTheme: Record<string, unknown>;
};

/**
 * A function that performs string translation within a namespace.
 *
 * @param key - Translation key relative to namespace
 * @param vars - Optional variables for interpolation
 * @param overrides - Optional translation overrides
 */
type TranslationFunction = (
  key: string,
  vars?: Record<string, unknown>,
  overrides?: Record<string, unknown>,
) => string;

/**
 * Factory function that creates namespace-scoped translation functions.
 *
 * @param namespace - Translation namespace (e.g., "mfa", "login")
 */
export type TFactory = (namespace: string) => TranslationFunction;

/** Defines the value provided by the i18n context. */
export interface I18nContextValue {
  /** The factory to create `t` functions. Null until initialized. */
  translator: TFactory | null;
  initialized: boolean;
}

'use client';

import * as React from 'react';
import type { Auth0ComponentConfig } from './types';
import { ProxyModeProvider } from './proxy-mode-provider';
import type { TFactory } from '@auth0-web-ui-components/core';
import { createI18n } from '@auth0-web-ui-components/core';
import { I18nContext } from './i18n-provider';
import { Spinner } from '@/components/ui/spinner';

const SpaModeProvider = React.lazy(() => import('./spa-mode-provider'));

/**
 * Auth0ComponentProvider
 *
 * The main Auth0 context provider component that conditionally
 * renders either the Proxy Mode or SPA Mode provider based on the presence
 * of `authProxyUrl`.
 *
 * - **Proxy Mode:** Used when authentication is handled externally via a proxy server.
 * - **SPA Mode:** Used when authentication is handled client-side using the Auth0 SPA SDK.
 *
 * This component abstracts the complexity of choosing the correct mode from the end user.
 *
 * @param {Object} props - Configuration props.
 * @param {string} [props.authProxyUrl] - Optional URL for proxy mode. When provided,
 *                                       enables proxy mode; otherwise, SPA mode is used.
 * @param {React.ReactNode} props.children - Child components that require authentication context.
 * @param {Object} [props.i18n] - Internationalization configuration (language, fallback).
 * @param {Object} [props.themeSettings] - Theme and branding settings.
 * @param {Object} [props.customOverrides] - Optional CSS variable overrides for styling.
 * @param {React.ReactNode} [props.loader] - Custom loading component to show while
 *                                                    authentication is initializing.
 *                                                    Defaults to "Loading authentication...".
 *
 * @returns {JSX.Element} The provider component for Auth0 context.
 *
 * @example
 * ```tsx
 * <Auth0ComponentProvider
 *   authProxyUrl="/api/auth"
 *   i18n={{ currentLanguage: 'en', fallbackLanguage: 'en' }}
 *   themeSettings={{ mode: 'dark' }}
 * >
 *   <App />
 * </Auth0ComponentProvider>
 * ```
 */
export const Auth0ComponentProvider = ({
  authProxyUrl,
  i18n,
  ...props
}: Auth0ComponentConfig & { children: React.ReactNode }) => {
  const isProxyMode = Boolean(authProxyUrl);
  const [i18nState, setI18nState] = React.useState<{
    initialized: boolean;
    translator: TFactory | null;
  }>({
    initialized: false,
    translator: null,
  });

  React.useEffect(() => {
    if (!i18n?.currentLanguage) {
      setI18nState({ initialized: true, translator: null });
      return;
    }

    const initializeTranslations = async () => {
      try {
        const instance = await createI18n({
          currentLanguage: i18n.currentLanguage,
          fallbackLanguage: i18n.fallbackLanguage,
        });

        setI18nState({
          initialized: true,
          translator: instance.t,
        });
      } catch {
        setI18nState({
          initialized: true,
          translator: null,
        });
      }
    };

    initializeTranslations();
  }, [i18n?.currentLanguage, i18n?.fallbackLanguage]);

  const i18nValue = React.useMemo(
    () => ({
      translator: i18nState.translator,
      initialized: i18nState.initialized,
    }),
    [i18nState.translator, i18nState.initialized],
  );

  return (
    <I18nContext.Provider value={i18nValue}>
      {isProxyMode ? (
        <ProxyModeProvider {...props} authProxyUrl={authProxyUrl} />
      ) : (
        <React.Suspense fallback={props.loader || <Spinner />}>
          <SpaModeProvider {...props} />
        </React.Suspense>
      )}
    </I18nContext.Provider>
  );
};

