import * as React from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { MailIcon, PhoneIcon } from 'lucide-react';

import {
  MFAType,
  FACTOR_TYPE_EMAIL,
  createEmailContactSchema,
  createSmsContactSchema,
  type EmailContactForm,
  type SmsContactForm,
  type EnrollMfaResponse,
} from '@auth0-web-ui-components/core';

import { Button } from '@/components/ui/button';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormControl,
  FormMessage,
} from '@/components/ui/form';
import { TextField } from '@/components/ui/text-field';
import { useTranslator } from '@/hooks';
import { useContactEnrollment } from '@/hooks/mfa';
import { EMAIL_PLACEHOLDER, PHONE_NUMBER_PLACEHOLDER, ENROLL } from '@/lib/mfa-constants';

type ContactForm = EmailContactForm | SmsContactForm;

type ContactInputFormProps = {
  factorType: MFAType;
  enrollMfa: (factor: MFAType, options: Record<string, string>) => Promise<EnrollMfaResponse>;
  onError: (error: Error, stage: typeof ENROLL) => void;
  onContactSuccess: (oobCode?: string) => void;
  onOtpSuccess: (otpData: {
    secret: string | null;
    barcodeUri: string | null;
    recoveryCodes: string[];
  }) => void;
};

export function ContactInputForm({
  factorType,
  enrollMfa,
  onError,
  onContactSuccess,
  onOtpSuccess,
}: ContactInputFormProps) {
  const { t } = useTranslator('mfa');

  const { onSubmitContact, loading } = useContactEnrollment({
    factorType,
    enrollMfa,
    onError,
    onContactSuccess,
    onOtpSuccess,
  });

  const ContactSchema = React.useMemo(() => {
    return factorType === FACTOR_TYPE_EMAIL
      ? createEmailContactSchema(t('errors.invalid_email'))
      : createSmsContactSchema(t('errors.invalid_phone_number'));
  }, [factorType, t]);

  const form = useForm<ContactForm>({
    resolver: zodResolver(ContactSchema),
    mode: 'onChange',
  });

  return (
    <div className="w-full max-w-sm mx-auto text-center">
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmitContact)} className="space-y-6">
          <FormField
            control={form.control}
            name="contact"
            render={({ field }) => (
              <FormItem>
                <FormLabel>
                  {factorType === FACTOR_TYPE_EMAIL
                    ? t('enrollment_form.email_address')
                    : t('enrollment_form.phone_number')}
                </FormLabel>
                <FormControl>
                  <TextField
                    type={factorType === FACTOR_TYPE_EMAIL ? 'email' : 'tel'}
                    startAdornment={
                      <div className="p-1.5">
                        {factorType === FACTOR_TYPE_EMAIL ? <MailIcon /> : <PhoneIcon />}
                      </div>
                    }
                    placeholder={
                      factorType === FACTOR_TYPE_EMAIL
                        ? EMAIL_PLACEHOLDER
                        : PHONE_NUMBER_PLACEHOLDER
                    }
                    error={Boolean(form.formState.errors.contact)}
                    {...field}
                  />
                </FormControl>
                <FormMessage className="text-left" />
              </FormItem>
            )}
          />
          <Button type="submit" size="sm" disabled={!form.formState.isValid || loading}>
            {loading ? t('enrollment_form.sending') : t('enrollment_form.send_code')}
          </Button>
        </form>
      </Form>
    </div>
  );
}

import * as React from 'react';
import { useForm } from 'react-hook-form';

import { Button } from '@/components/ui/button';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormControl,
  FormMessage,
} from '@/components/ui/form';
import { OTPField } from '@/components/ui/otp-field';
import { useTranslator } from '@/hooks';
import { useOtpConfirmation } from '@/hooks/mfa';
import { type MFAType } from '@auth0-web-ui-components/core';
import { CONFIRM } from '@/lib/mfa-constants';

type OtpForm = {
  userOtp: string;
};

type OTPVerificationFormProps = {
  factorType: MFAType;
  confirmEnrollment: (
    factor: MFAType,
    options: { oobCode?: string; userOtpCode?: string; userEmailOtpCode?: string },
  ) => Promise<unknown | null>;
  onError: (error: Error, stage: typeof CONFIRM) => void;
  onSuccess: () => void;
  onClose: () => void;
  oobCode?: string;
};

export function OTPVerificationForm({
  factorType,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
  oobCode,
}: OTPVerificationFormProps) {
  const { t } = useTranslator('mfa');

  const { onSubmitOtp, loading } = useOtpConfirmation({
    factorType,
    confirmEnrollment,
    onError,
    onSuccess,
    onClose,
  });

  const form = useForm<OtpForm>({
    mode: 'onChange',
  });

  const handleSubmit = React.useCallback(
    (data: OtpForm) => {
      onSubmitOtp(data, oobCode);
    },
    [onSubmitOtp, oobCode],
  );

  return (
    <div className="w-full max-w-sm mx-auto text-center">
      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleSubmit)} autoComplete="off" className="space-y-6">
          <FormField
            control={form.control}
            name="userOtp"
            render={({ field }) => (
              <FormItem className="text-center">
                <FormLabel className="block w-full text-sm font-medium text-center">
                  {t('enrollment_form.show_otp.enter_verify_code')}
                </FormLabel>
                <FormControl>
                  <div className="flex justify-center">
                    <OTPField length={6} onChange={field.onChange} className="max-w-xs" />
                  </div>
                </FormControl>
                <FormMessage className="text-left" />
              </FormItem>
            )}
          />
          <Button type="submit" size="sm" disabled={loading}>
            {loading
              ? t('enrollment_form.show_otp.verifying')
              : t('enrollment_form.show_otp.verify_code')}
          </Button>
        </form>
      </Form>
    </div>
  );
}

import * as React from 'react';
import { useForm } from 'react-hook-form';
import QRCode from 'react-qr-code';

import { Button } from '@/components/ui/button';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormControl,
  FormMessage,
} from '@/components/ui/form';
import { OTPField } from '@/components/ui/otp-field';
import { useTranslator } from '@/hooks';
import { useOtpConfirmation } from '@/hooks/mfa';
import { type MFAType } from '@auth0-web-ui-components/core';
import { CONFIRM } from '@/lib/mfa-constants';

type OtpForm = {
  userOtp: string;
};

type QRCodeEnrollmentFormProps = {
  factorType: MFAType;
  barcodeUri: string;
  recoveryCodes: string[];
  confirmEnrollment: (
    factor: MFAType,
    options: { oobCode?: string; userOtpCode?: string; userEmailOtpCode?: string },
  ) => Promise<unknown | null>;
  onError: (error: Error, stage: typeof CONFIRM) => void;
  onSuccess: () => void;
  onClose: () => void;
  oobCode?: string;
};

export function QRCodeEnrollmentForm({
  factorType,
  barcodeUri,
  recoveryCodes,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
  oobCode,
}: QRCodeEnrollmentFormProps) {
  const { t } = useTranslator('mfa');

  const { onSubmitOtp, loading } = useOtpConfirmation({
    factorType,
    confirmEnrollment,
    onError,
    onSuccess,
    onClose,
  });

  const form = useForm<OtpForm>({
    mode: 'onChange',
  });

  const handleSubmit = React.useCallback(
    (data: OtpForm) => {
      onSubmitOtp(data, oobCode);
    },
    [onSubmitOtp, oobCode],
  );

  return (
    <div className="text-center">
      <p>{t('enrollment_form.show_otp.title')}</p>
      <div className="flex justify-center items-center mt-6">
        <div className="border border-gray-300 p-4 rounded-lg shadow-lg bg-white inline-block">
          <QRCode
            size={150}
            style={{ height: 'auto', maxWidth: '100%', width: '100%' }}
            value={barcodeUri || ''}
            viewBox={`0 0 150 150`}
          />
        </div>
      </div>
      <div className="mt-6">
        {recoveryCodes.length > 0 && (
          <div className="mb-6">
            <p>
              <strong>{t('enrollment_form.show_otp.save_recovery')}</strong>
            </p>
            <ul className="list-none inline-block bg-gray-100 dark:bg-gray-800 p-4 rounded-lg mt-2">
              {recoveryCodes.map((code, index) => (
                <li key={index} className="font-mono tracking-widest">
                  {code}
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="w-full max-w-sm mx-auto text-center">
          <Form {...form}>
            <form
              autoComplete="off"
              onSubmit={form.handleSubmit(handleSubmit)}
              className="space-y-6 mt-4"
            >
              <FormField
                control={form.control}
                name="userOtp"
                render={({ field }) => (
                  <FormItem className="text-center">
                    <FormLabel className="block w-full text-sm font-medium text-center">
                      {t('enrollment_form.show_otp.enter_code')}
                    </FormLabel>
                    <FormControl>
                      <div className="flex justify-center">
                        <OTPField length={6} onChange={field.onChange} className="max-w-xs" />
                      </div>
                    </FormControl>
                    <FormMessage className="text-left" />
                  </FormItem>
                )}
              />
              <Button type="submit" size="sm" disabled={loading}>
                {loading
                  ? t('enrollment_form.show_otp.verifying')
                  : t('enrollment_form.show_otp.verify_code')}
              </Button>
            </form>
          </Form>
        </div>
      </div>
    </div>
  );
}

import * as React from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { ContactInputForm } from './contact-input-form';
import { QRCodeEnrollmentForm } from './qr-code-enrollment-form';
import { OTPVerificationForm } from './otp-verification-form';
import { SHOW_OTP, ENTER_OTP, ENTER_CONTACT, ENROLL, CONFIRM } from '@/lib/mfa-constants';
import {
  FACTOR_TYPE_EMAIL,
  FACTOR_TYPE_SMS,
  FACTOR_TYPE_TOPT,
  FACTOR_TYPE_PUSH_NOTIFICATION,
} from '@auth0-web-ui-components/core';
import { type MFAType, type EnrollMfaResponse } from '@auth0-web-ui-components/core';
import { useTranslator } from '@/hooks';
import { useOtpEnrollment } from '@/hooks/mfa';

type UserMFASetupFormProps = {
  open: boolean;
  onClose: () => void;
  factorType: MFAType;
  enrollMfa: (factor: MFAType, options: Record<string, string>) => Promise<EnrollMfaResponse>;
  confirmEnrollment: (
    factor: MFAType,
    options: { oobCode?: string; userOtpCode?: string; userEmailOtpCode?: string },
  ) => Promise<unknown | null>;
  onSuccess: () => void;
  onError: (error: Error, stage: typeof ENROLL | typeof CONFIRM) => void;
};

type EnrollmentPhase = typeof ENTER_CONTACT | typeof ENTER_OTP | typeof SHOW_OTP | null;

export function UserMFASetupForm({
  open,
  onClose,
  factorType,
  enrollMfa,
  confirmEnrollment,
  onSuccess,
  onError,
}: UserMFASetupFormProps) {
  const { t } = useTranslator('mfa');

  // Initialize phase as null, meaning no UI shown by default
  const [phase, setPhase] = React.useState<EnrollmentPhase>(null);
  const [oobCode, setOobCode] = React.useState<string | undefined>(undefined);

  const otpEnrollment = useOtpEnrollment({
    factorType,
    enrollMfa,
    onError,
    onClose,
  });

  React.useEffect(() => {
    if (!open) {
      setPhase(null); // reset phase to null when dialog closes
      setOobCode(undefined);
      otpEnrollment.resetOtpData();
    }
  }, [open, otpEnrollment]);

  React.useEffect(() => {
    if (open && (factorType === FACTOR_TYPE_EMAIL || factorType === FACTOR_TYPE_SMS)) {
      setPhase(ENTER_CONTACT);
    }
  }, [open, factorType]);

  // Automatically initiate OTP enrollment when factorType is 'totp' or 'push-notification'
  React.useEffect(() => {
    if (
      [FACTOR_TYPE_TOPT, FACTOR_TYPE_PUSH_NOTIFICATION].includes(factorType) &&
      !otpEnrollment.otpData.secret &&
      open
    ) {
      otpEnrollment.fetchOtpEnrollment().then(() => {
        setPhase(SHOW_OTP);
      });
    }
  }, [factorType, otpEnrollment, open]);

  // Callback functions for form components
  const handleContactSuccess = React.useCallback((oobCode?: string) => {
    setOobCode(oobCode);
    setPhase(ENTER_OTP);
  }, []);

  const handleOtpSuccess = React.useCallback(
    (otpData: { secret: string | null; barcodeUri: string | null; recoveryCodes: string[] }) => {
      otpEnrollment.resetOtpData();
      // Use the proper setter function instead of direct mutation
      otpEnrollment.updateOtpData(otpData);
      setPhase(SHOW_OTP);
    },
    [otpEnrollment],
  );

  // Render the appropriate form based on the current phase and factorType
  const renderForm = () => {
    switch (phase) {
      case ENTER_CONTACT:
        return (
          <ContactInputForm
            factorType={factorType}
            enrollMfa={enrollMfa}
            onError={onError}
            onContactSuccess={handleContactSuccess}
            onOtpSuccess={handleOtpSuccess}
          />
        );
      case SHOW_OTP:
        return (
          <QRCodeEnrollmentForm
            factorType={factorType}
            barcodeUri={otpEnrollment.otpData.barcodeUri || ''}
            recoveryCodes={otpEnrollment.otpData.recoveryCodes}
            confirmEnrollment={confirmEnrollment}
            onError={onError}
            onSuccess={onSuccess}
            onClose={onClose}
            oobCode={oobCode}
          />
        );
      case ENTER_OTP:
        return (
          <OTPVerificationForm
            factorType={factorType}
            confirmEnrollment={confirmEnrollment}
            onError={onError}
            onSuccess={onSuccess}
            onClose={onClose}
            oobCode={oobCode}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Dialog open={open && Boolean(phase)} onOpenChange={onClose}>
      <DialogContent aria-describedby={factorType}>
        <DialogHeader>
          <DialogTitle className="text-center">
            {factorType === FACTOR_TYPE_EMAIL
              ? t('enrollment_form.enroll_email')
              : factorType === FACTOR_TYPE_SMS
                ? t('enrollment_form.enroll_sms')
                : t('enroll_otp_mfa')}
          </DialogTitle>
        </DialogHeader>
        {renderForm()}
      </DialogContent>
    </Dialog>
  );
}

export { useComponentConfig, Auth0ComponentConfigContext } from './use-config';
export { useCoreClient, CoreClientContext } from './use-core-client';
export { useTranslator } from './use-translator';
export { useContactEnrollment, useOtpConfirmation, useOtpEnrollment, useMFA } from './mfa';
export { useTheme } from './use-theme';
export { useCoreClientInitialization } from './use-core-client-initialization';

export { useContactEnrollment } from './use-contact-enrollment';
export { useOtpConfirmation } from './use-otp-confirmation';
export { useOtpEnrollment } from './use-otp-enrollment';
export { useMFA } from './use-mfa';

export const ENTER_OTP = 'enterOtp';
export const SHOW_OTP = 'showOtp';
export const ENTER_CONTACT = 'enterContact';

export const ENROLL = 'enroll';
export const CONFIRM = 'confirm';
export const EMAIL_PLACEHOLDER = 'your@email.com';
export const PHONE_NUMBER_PLACEHOLDER = '+1234567890';

import * as React from 'react';

import { useComponentConfig, useMFA, useTranslator } from '@/hooks';
import type { UserMFAMgmtProps } from '@/types';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Toaster } from '@/components/ui/sonner';
import { ENROLL, CONFIRM } from '@/lib/mfa-constants';
import { UserMFASetupForm } from '@/components/mfa/user-mfa-setup-form';
import { Spinner } from '@/components/ui/spinner';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { withCoreClient } from '@/hoc';
import { Authenticator, MFAType } from '@auth0-web-ui-components/core';

/**
 * UserMFAMgmt Component
 *
 * A component responsible for managing Multi-Factor Authentication (MFA) factors for a user.
 * This component handles the mfa access token fetching ,fetching authenticators, enrolling, and deletion of MFA factors and manages the MFA access token.
 * It operates in both ProxyMode (RWA) and SPA modes for authentication.
 *
 * - **ProxyMode (RWA)**: In this mode, the component interacts with a proxy service to manage MFA
 * - **SPA (Single Page Application)**: In this mode, the component communicates directly with the API to manage MFA factors.
 * * @param {Object} props - The properties passed to the component.
 * @param {Object} [props.customMessages] - Custom messages to override default translations for this component instance.
 * @param {boolean} [props.hideHeader=false] - Whether to hide the header.
 * @param {boolean} [props.showActiveOnly=false] - Whether to show only active MFA factors.
 * @param {boolean} [props.disableEnroll=false] - Whether to disable the enrollment of new factors.
 * @param {boolean} [props.disableDelete=false] - Whether to disable the deletion of factors.
 * @param {boolean} [props.readOnly=false] - Whether the component is in read-only mode.
 * @param {Object} [props.factorConfig={}] - Configuration for MFA factors, controlling visibility and enabled state.
 * @param {Function} [props.onEnroll] - Callback fired when an MFA factor is successfully enrolled.
 * @param {Function} [props.onDelete] - Callback fired when an MFA factor is successfully deleted.
 * @param {Function} [props.onFetch] - Callback fired when MFA factors are fetched.
 * @param {Function} [props.onErrorAction] - Callback fired when an error occurs during an action (enroll/delete).
 * @param {Function} [props.onBeforeAction] - Callback fired before performing an action (enroll/delete).
 *
 * @returns {React.JSX.Element} The rendered component.
 */
function UserMFAMgmtComponent({
  customMessages = {},
  hideHeader = false,
  showActiveOnly = false,
  disableEnroll = false,
  disableDelete = false,
  readOnly = false,
  factorConfig = {},
  onEnroll,
  onDelete,
  onFetch,
  onErrorAction,
  onBeforeAction,
}: UserMFAMgmtProps): React.JSX.Element {
  const { t } = useTranslator('mfa', customMessages);
  const {
    config: { loader },
  } = useComponentConfig();
  const { fetchFactors, enrollMfa, deleteMfa, confirmEnrollment } = useMFA();

  const [factors, setFactors] = React.useState<Authenticator[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [isDeletingFactor, setIsDeletingFactor] = React.useState(false);
  const [dialogOpen, setDialogOpen] = React.useState(false);
  const [enrollFactor, setEnrollFactor] = React.useState<MFAType | null>(null);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = React.useState(false);
  const [factorToDelete, setFactorToDelete] = React.useState<{
    id: string;
    type: MFAType;
  } | null>(null);

  /**
   * Loads the available MFA factors from the API and updates the state.
   * This is called on initial load and when factors need to be refreshed.
   */
  const loadFactors = React.useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const factors = await fetchFactors(showActiveOnly);
      setFactors(factors);
      onFetch?.();
    } catch (err) {
      setError(t('errors.factors_loading_error'));
    }

    setLoading(false);
  }, [fetchFactors, showActiveOnly, onFetch, onErrorAction]);

  React.useEffect(() => {
    loadFactors();
  }, [loadFactors]);

  /**
   * Filters visible MFA factors based on the provided factor configuration.
   * Each factor's visibility is determined by the configuration settings.
   *
   * @returns {Authenticator[]} Filtered MFA factors.
   */
  const visibleFactors = React.useMemo(() => {
    return factors.filter((factor) => {
      const config = factorConfig[factor.factorName as keyof typeof factorConfig];
      return config?.visible !== false;
    });
  }, [factors, factorConfig]);

  /**
   * Handles the enrollment button click for a specific MFA factor.
   * Opens the enrollment dialog for the chosen factor.
   *
   * @param {MFAType} factor - The MFA factor to be enrolled.
   */
  const handleEnrollClick = (factor: MFAType) => {
    setEnrollFactor(factor);
    setDialogOpen(true);
  };

  const handleCloseDialog = React.useCallback(() => {
    setDialogOpen(false);
    setEnrollFactor(null);
  }, []);

  /**
   * Handles the initial click on the delete button for an MFA factor.
   * This function either:
   * 1. Triggers the onBeforeAction callback and proceeds with deletion if approved
   * 2. Opens a confirmation dialog if no onBeforeAction is provided
   *
   * The function prevents deletion if:
   * - Component is in readonly mode
   * - Delete action is disabled
   * - onBeforeAction returns false
   *
   * @param {string} factorId - The unique identifier of the MFA factor to delete
   * @param {MFAType} factorType - The type of MFA factor being deleted (e.g., 'sms', 'email', 'totp')
   * @returns {Promise<void>}
   */
  const handleDeleteClick = React.useCallback(
    async (factorId: string, factorType: MFAType) => {
      if (readOnly || disableDelete) return;

      if (onBeforeAction) {
        // If onBeforeAction exists, proceed directly
        const canProceed = await onBeforeAction('delete', factorType);
        if (!canProceed) return;
        await handleConfirmDelete(factorId);
      } else {
        setFactorToDelete({ id: factorId, type: factorType });
        setIsDeleteDialogOpen(true);
      }
    },
    [readOnly, disableDelete, onBeforeAction],
  );

  /**
   * Handles the confirmation and execution of MFA factor deletion.
   * This callback is triggered when a user confirms the deletion in the confirmation dialog
   * or when deletion is approved through onBeforeAction.
   *
   * The function:
   * 1. Deletes the MFA factor
   * 2. Reloads the factors list
   * 3. Shows success/error notifications
   * 4. Handles cleanup of dialog and loading states
   *
   * @param {string} factorId - The unique identifier of the MFA factor to delete
   * @throws {Error} When deletion fails or factors cannot be reloaded
   */
  const handleConfirmDelete = React.useCallback(
    async (factorId: string) => {
      setIsDeletingFactor(true);

      const cleanUp = () => {
        setIsDeletingFactor(false);
        setIsDeleteDialogOpen(false);
        setFactorToDelete(null);
      };

      try {
        await deleteMfa(factorId);
      } catch (err) {
        const error = err instanceof Error ? err : new Error(t('errors.delete_factor'));
        toast.error(t('errors.delete_factor'));
        onErrorAction?.(error, 'delete');
        cleanUp();
        return;
      }

      toast.success(t('remove_factor'), {
        duration: 2000,
        onAutoClose: () => onDelete?.(),
      });

      try {
        await loadFactors();
      } catch (err) {
        const error = err instanceof Error ? err : new Error(t('errors.factors_loading_error'));
        onErrorAction?.(error, 'delete');
      } finally {
        cleanUp();
      }
    },
    [deleteMfa, loadFactors, onDelete, onErrorAction, t],
  );

  /**
   * Handles the successful enrollment of an MFA factor.
   * Displays a success message and reloads the factors list.
   */
  const handleEnrollSuccess = React.useCallback(async () => {
    setDialogOpen(false);
    setEnrollFactor(null);
    try {
      toast.success(t('enroll_factor'), {
        duration: 2000,
        onAutoClose: () => {
          onEnroll?.();
        },
      });
      await loadFactors();
    } catch {
      toast.dismiss();
      toast.error(t('errors.factors_loading_error'));
    }
  }, [loadFactors, onEnroll, t]);

  /**
   * Handles errors during the enrollment or confirmation process.
   *
   * @param {Error} error - The error object containing the failure message.
   * @param {string} stage - The stage of the process ('enroll' or 'confirm').
   */
  const handleEnrollError = React.useCallback(
    (error: Error, stage: typeof ENROLL | typeof CONFIRM) => {
      toast.error(
        `${stage === ENROLL ? t('enrollment') : t('confirmation')} ${t('errors.failed', { message: error.message })}`,
      );
      onErrorAction?.(error, stage);
    },
    [onErrorAction, t],
  );

  return (
    <>
      <Toaster position="top-right" />
      {loading ? (
        loader || <Spinner />
      ) : error ? (
        <div className="flex items-center justify-center p-4">
          <Label className="text-center text-destructive">{error}</Label>
        </div>
      ) : (
        <Card>
          {!hideHeader && (
            <CardHeader>
              <CardTitle>{t('title')}</CardTitle>
              <CardDescription>{t('description')}</CardDescription>
            </CardHeader>
          )}

          <CardContent className="grid gap-6 p-4 pt-0 md:p-6 md:pt-0">
            {showActiveOnly && visibleFactors.length === 0 ? (
              <Label className="text-center text-muted-foreground">{t('no_active_mfa')}</Label>
            ) : (
              visibleFactors.map((factor, idx) => {
                const isEnabledFactor =
                  factorConfig?.[factor.factorName as MFAType]?.enabled !== false;

                return (
                  <div
                    key={`${factor.name}-${idx}`}
                    className={`flex flex-col gap-6 ${!isEnabledFactor ? 'opacity-50 pointer-events-none' : ''}`}
                    aria-disabled={!isEnabledFactor}
                  >
                    {idx > 0 && <Separator />}
                    <div className="flex flex-col items-center justify-between space-y-6 md:flex-row md:space-x-2 md:space-y-0">
                      <Label className="flex flex-col items-start space-y-1">
                        <span className="leading-6 text-left">
                          {t(`${factor.factorName}.title`)}
                          {factor.active && (
                            <Badge variant="success" size="sm" className="ml-3">
                              {t('enrolled')}
                            </Badge>
                          )}
                        </span>
                        <p className="font-normal leading-snug text-muted-foreground text-left">
                          {t(`${factor.factorName}.description`)}
                        </p>
                      </Label>

                      <div className="flex items-center justify-end space-x-24 md:min-w-72">
                        {factor.active
                          ? !readOnly && (
                              <Button
                                type="submit"
                                size="sm"
                                onClick={() =>
                                  handleDeleteClick(factor.id, factor.factorName as MFAType)
                                }
                                disabled={disableDelete || isDeletingFactor || !isEnabledFactor}
                                aria-label={t('delete_factor', { factorName: factor.factorName })}
                              >
                                {t('delete')}
                              </Button>
                            )
                          : !readOnly && (
                              <Button
                                size="sm"
                                onClick={() => handleEnrollClick(factor.factorName as MFAType)}
                                disabled={disableEnroll || !isEnabledFactor}
                              >
                                {t('enroll')}
                              </Button>
                            )}
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </CardContent>
        </Card>
      )}
      {enrollFactor && (
        <UserMFASetupForm
          open={dialogOpen}
          onClose={handleCloseDialog}
          factorType={enrollFactor}
          enrollMfa={enrollMfa}
          confirmEnrollment={confirmEnrollment}
          onSuccess={handleEnrollSuccess}
          onError={handleEnrollError}
        />
      )}
      <Dialog
        open={isDeleteDialogOpen}
        onOpenChange={(open) => !isDeletingFactor && setIsDeleteDialogOpen(open)}
      >
        <DialogContent aria-describedby="delete-mfa-description">
          <DialogHeader>
            <DialogTitle className="text-center">{t('delete_mfa_title')}</DialogTitle>
          </DialogHeader>
          <div className="py-4">
            <p id="delete-mfa-description" className="text-center text-muted-foreground">
              {t('delete_mfa_content')}
            </p>
          </div>
          <div className="flex justify-end gap-4 pt-4">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setIsDeleteDialogOpen(false)}
              disabled={isDeletingFactor}
            >
              {t('cancel')}
            </Button>
            <Button
              variant="destructive"
              onClick={() => factorToDelete && handleConfirmDelete(factorToDelete.id)}
              disabled={isDeletingFactor}
            >
              {isDeletingFactor ? t('deleting') : t('delete')}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

