                <FormControl>
                  <TextField
                    type={factorType === FACTOR_TYPE_EMAIL ? 'email' : 'tel'}
                    startAdornment={
                      <div className="p-1.5">
                        {factorType === FACTOR_TYPE_EMAIL ? <MailIcon /> : <PhoneIcon />}
