import { useState, useCallback } from 'react';
import {
  FACTOR_TYPE_EMAIL,
  FACTOR_TYPE_SMS,
  FACTOR_TYPE_OTP,
  normalizeError,
  type MFAType,
  type EnrollMfaResponse,
} from '@auth0-web-ui-components/core';
import { ENROLL } from '@/lib/mfa-constants';
import { useTranslator } from '@/hooks';

type ContactForm = {
  contact: string;
};

type UseContactEnrollmentProps = {
  factorType: MFAType;
  enrollMfa: (factor: MFAType, options: Record<string, string>) => Promise<EnrollMfaResponse>;
  onError: (error: Error, stage: typeof ENROLL) => void;
  onContactSuccess: (oobCode?: string) => void;
  onOtpSuccess: (otpData: {
    secret: string | null;
    barcodeUri: string | null;
    recoveryCodes: string[];
  }) => void;
};

export function useContactEnrollment({
  factorType,
  enrollMfa,
  onError,
  onContactSuccess,
  onOtpSuccess,
}: UseContactEnrollmentProps) {
  const { t } = useTranslator('mfa');
  const [loading, setLoading] = useState(false);

  const onSubmitContact = useCallback(
    async (data: ContactForm) => {
      setLoading(true);
      try {
        const options: Record<string, string> =
          factorType === FACTOR_TYPE_EMAIL
            ? { email: data.contact }
            : factorType === FACTOR_TYPE_SMS
              ? { phone_number: data.contact }
              : {};

        const response = await enrollMfa(factorType, options);

        if (response?.oob_code) {
          onContactSuccess(response.oob_code);
        }

        if (response?.authenticator_type === FACTOR_TYPE_OTP) {
          onOtpSuccess({
            secret: response.secret ?? null,
            barcodeUri: response.barcode_uri ?? null,
            recoveryCodes: response.recovery_codes || [],
          });
        }
      } catch (error) {
        const normalizedError = normalizeError(error, {
          resolver: (code) => t(`errors.${code}.${factorType}`),
          fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
        });
        onError(normalizedError, ENROLL);
      } finally {
        setLoading(false);
      }
    },
    [factorType, enrollMfa, onContactSuccess, onOtpSuccess, onError, t],
  );

  return { onSubmitContact, loading };
}

import { useCallback } from 'react';
import { useCoreClient } from '../use-core-client';
import type {
  MFAType,
  EnrollOptions,
  ConfirmEnrollmentOptions,
} from '@auth0-web-ui-components/core';
import { UseMFAResult } from '@/types';

/**
 * A custom React hook for managing all Multi-Factor Authentication (MFA) operations.
 * @returns {UseMfaResult} An object containing the functions to manage MFA factors.
 */
export function useMFA(): UseMFAResult {
  const { coreClient } = useCoreClient();

  if (!coreClient) {
    throw new Error(
      'useMFA must be used within Auth0ComponentProvider with initialized CoreClient',
    );
  }

  const fetchFactors = useCallback(
    (onlyActive = false) => coreClient.authenticationApiService.mfa.fetchFactors(onlyActive),
    [coreClient],
  );

  const enrollMfa = useCallback(
    (factorName: MFAType, options: EnrollOptions = {}) =>
      coreClient.authenticationApiService.mfa.enrollFactor(factorName, options),
    [coreClient],
  );

  const deleteMfa = useCallback(
    (authenticatorId: string) =>
      coreClient.authenticationApiService.mfa.deleteFactor(authenticatorId),
    [coreClient],
  );

  const confirmEnrollment = useCallback(
    (factorName: MFAType, options: ConfirmEnrollmentOptions) =>
      coreClient.authenticationApiService.mfa.confirmEnrollment(factorName, options),
    [coreClient],
  );

  return {
    fetchFactors,
    enrollMfa,
    deleteMfa,
    confirmEnrollment,
  };
}

import { useState, useCallback } from 'react';
import { FACTOR_TYPE_EMAIL, normalizeError, type MFAType } from '@auth0-web-ui-components/core';
import { CONFIRM } from '@/lib/mfa-constants';
import { useTranslator } from '@/hooks';

type OtpForm = {
  userOtp: string;
};

type UseOtpConfirmationProps = {
  factorType: MFAType;
  confirmEnrollment: (
    factor: MFAType,
    options: { oobCode?: string; userOtpCode?: string; userEmailOtpCode?: string },
  ) => Promise<unknown | null>;
  onError: (error: Error, stage: typeof CONFIRM) => void;
  onSuccess: () => void;
  onClose: () => void;
};

export function useOtpConfirmation({
  factorType,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
}: UseOtpConfirmationProps) {
  const { t } = useTranslator('mfa');
  const [loading, setLoading] = useState(false);

  const onSubmitOtp = useCallback(
    async (data: OtpForm, oobCode?: string) => {
      setLoading(true);

      try {
        const options = {
          oobCode,
          ...(factorType === FACTOR_TYPE_EMAIL
            ? { userEmailOtpCode: data.userOtp }
            : { userOtpCode: data.userOtp }),
        };

        const response = await confirmEnrollment(factorType, options);
        if (response) {
          onSuccess();
          onClose();
        }
      } catch (err) {
        const normalizedError = normalizeError(err, {
          resolver: (code) => t(`errors.${code}.${factorType}`),
          fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
        });
        onError(normalizedError, CONFIRM);
      } finally {
        setLoading(false);
      }
    },
    [factorType, confirmEnrollment, onError, onSuccess, onClose, t],
  );

  return { onSubmitOtp, loading };
}

import { useState, useCallback } from 'react';
import {
  FACTOR_TYPE_OTP,
  normalizeError,
  type MFAType,
  type EnrollMfaResponse,
} from '@auth0-web-ui-components/core';
import { ENROLL } from '@/lib/mfa-constants';
import { useTranslator } from '@/hooks';

type UseOtpEnrollmentProps = {
  factorType: MFAType;
  enrollMfa: (factor: MFAType, options: Record<string, string>) => Promise<EnrollMfaResponse>;
  onError: (error: Error, stage: typeof ENROLL) => void;
  onClose: () => void;
};

export function useOtpEnrollment({
  factorType,
  enrollMfa,
  onError,
  onClose,
}: UseOtpEnrollmentProps) {
  const { t } = useTranslator('mfa');
  const [loading, setLoading] = useState(false);
  const [otpData, setOtpData] = useState<{
    secret: string | null;
    barcodeUri: string | null;
    recoveryCodes: string[];
  }>({
    secret: null,
    barcodeUri: null,
    recoveryCodes: [],
  });

  const fetchOtpEnrollment = useCallback(async () => {
    setLoading(true);
    try {
      const response = await enrollMfa(factorType, {});
      if (response?.authenticator_type === FACTOR_TYPE_OTP) {
        setOtpData({
          secret: response.secret ?? null,
          barcodeUri: response.barcode_uri ?? null,
          recoveryCodes: response.recovery_codes || [],
        });
      }
    } catch (error) {
      const normalizedError = normalizeError(error, {
        resolver: (code) => t(`errors.${code}.${factorType}`),
        fallbackMessage: 'An unexpected error occurred during MFA enrollment.',
      });
      onError(normalizedError, ENROLL);
      onClose();
    } finally {
      setLoading(false);
    }
  }, [factorType, enrollMfa, onError, onClose, t]);

  const resetOtpData = useCallback(() => {
    setOtpData({ secret: null, barcodeUri: null, recoveryCodes: [] });
    setLoading(false);
  }, []);

  const updateOtpData = useCallback(
    (newData: { secret: string | null; barcodeUri: string | null; recoveryCodes: string[] }) => {
      setOtpData(newData);
    },
    [],
  );

  return {
    fetchOtpEnrollment,
    otpData,
    resetOtpData,
    updateOtpData,
    loading,
  };
}

import * as React from 'react';
import type { Auth0ComponentConfig } from '@/types/auth-types';

export const Auth0ComponentConfigContext = React.createContext<{
  config: Auth0ComponentConfig;
}>({
  config: {
    themeSettings: { mode: 'light' },
    customOverrides: {},
    loader: undefined,
  },
});

/**
 * Hook to access the Auth0 component configuration from context.
 */
export function useComponentConfig(): { config: Auth0ComponentConfig } {
  const context = React.useContext(Auth0ComponentConfigContext);

  if (!context) {
    throw new Error('useComponentConfig must be used within an Auth0ComponentProvider');
  }

  return context;
}

import * as React from 'react';
import type {
  CoreClientInterface,
  AuthDetailsCore,
  I18nInitOptions,
} from '@auth0-web-ui-components/core';
import { createCoreClient } from '@auth0-web-ui-components/core';

interface UseCoreClientInitializationProps {
  authDetails: AuthDetailsCore;
  i18nOptions?: I18nInitOptions;
}

/**
 * Custom hook to handle CoreClient initialization
 */
export const useCoreClientInitialization = ({
  authDetails,
  i18nOptions,
}: UseCoreClientInitializationProps) => {
  const [coreClient, setCoreClient] = React.useState<CoreClientInterface | null>(null);

  React.useEffect(() => {
    const initializeCoreClient = async () => {
      try {
        const initializedCoreClient = await createCoreClient(authDetails, i18nOptions);

        setCoreClient(initializedCoreClient);
      } catch (error) {
        console.error('Failed to initialize CoreClient:', error);
        setCoreClient(null);
      }
    };

    initializeCoreClient();
  }, [
    authDetails.contextInterface?.getAccessTokenSilently,
    i18nOptions?.currentLanguage,
    i18nOptions?.fallbackLanguage,
  ]);

  return coreClient;
};

import * as React from 'react';
import type { CoreClientInterface } from '@auth0-web-ui-components/core';

const CoreClientContext = React.createContext<{
  coreClient: CoreClientInterface | null;
}>({
  coreClient: null,
});

/**
 * Hook to access the CoreClient instance from context.
 *
 * Provides access to the initialized CoreClient which handles API calls and business logic.
 *
 * @returns {{ coreClient: CoreClientInterface | null }} The current CoreClient instance or null if not initialized.
 *
 * @throws {Error} Throws if used outside of an Auth0ComponentProvider.
 */
export const useCoreClient = () => {
  const context = React.useContext(CoreClientContext);
  if (!context) {
    throw new Error('useCoreClient must be used within Auth0ComponentProvider');
  }
  return context;
};

export { CoreClientContext };

