import * as React from 'react';
import { AuthDetailsCore } from '@auth0-web-ui-components/core';
import { I18nOptions } from './i18n-types';
import { CustomOverrides, ThemeSettings } from './theme-types';

/**
 * Auth0 authentication details with optional React-specific properties.
 */
export type AuthDetails = Omit<AuthDetailsCore, 'accessToken'>;

/**
 * Props for the Auth0ComponentProvider component.
 */
export interface Auth0ComponentProviderProps {
  i18n?: I18nOptions;
  themeSettings?: ThemeSettings;
  authDetails?: AuthDetails;
  customOverrides?: CustomOverrides;
  loader?: React.ReactNode;
}

/**
 * Props for the InternalProvider component.
 */
export interface InternalProviderProps {
  i18n?: I18nOptions;
  authDetails?: AuthDetails;
}

/**
 * Configuration for Auth0ComponentProvider excluding authentication details.
 */
export type Auth0ComponentConfig = Omit<Auth0ComponentProviderProps, 'authDetails' | 'i18n'>;

/**
 * Internationalization configuration for Auth0 components.
 */
export interface I18nOptions {
  currentLanguage: string;
  fallbackLanguage?: string;
}

import type {
  EnrollMfaResponse,
  Authenticator,
  MFAType,
  EnrollOptions,
  ConfirmEnrollmentOptions,
  MFAMessages,
} from '@auth0-web-ui-components/core';

export interface UserMFAMgmtProps {
  customMessages?: Partial<MFAMessages>;
  hideHeader?: boolean;
  showActiveOnly?: boolean;
  disableEnroll?: boolean;
  disableDelete?: boolean;
  readOnly?: boolean;
  factorConfig?: {
    [key in MFAType]?: {
      visible?: boolean;
      enabled?: boolean;
    };
  };
  onEnroll?: () => void;
  onDelete?: () => void;
  onFetch?: () => void;
  onErrorAction?: (error: Error, action: 'enroll' | 'delete' | 'confirm') => void;
  onBeforeAction?: (
    action: 'enroll' | 'delete' | 'confirm',
    factorType: MFAType,
  ) => boolean | Promise<boolean>;
}

/**
 * Result returned by the `useMFA` hook.
 * Provides methods to fetch, enroll, and delete MFA authenticators.
 */
export type UseMFAResult = {
  /**
   * Fetch the list of MFA authenticators.
   * @param onlyActive - Whether to return only active authenticators.
   * @returns A promise resolving to array of authenticators.
   */
  fetchFactors: (onlyActive?: boolean) => Promise<Authenticator[]>;

  /**
   * Enroll a new MFA factor (e.g., SMS, TOTP, Email).
   * @param factorName - The type of MFA to enroll.
   * @param options - Optional options like phone number or email.
   * @returns A promise resolving to the enrollment response.
   */
  enrollMfa: (factorName: MFAType, options?: EnrollOptions) => Promise<EnrollMfaResponse>;

  /**
   * Delete an enrolled MFA authenticator by its ID.
   * @param authenticatorId - The ID of the authenticator to delete.
   * @returns A promise resolving to a success flag.
   */
  deleteMfa: (authenticatorId: string) => Promise<void>;

  /**
   * Confirm MFA enrollment with OOB code and user OTP code.
   * @param factorName The MFA factor type.
   * @param options The confirmation codes required to verify enrollment.
   */
  confirmEnrollment: (factorName: MFAType, options: ConfirmEnrollmentOptions) => Promise<unknown>;
};

/**
 * Theme configuration
 * @property {('light'|'dark')} [mode] - Theme mode
 * @property {string} [primaryColor] - Primary color for theming
 */
export interface ThemeSettings {
  mode?: 'light' | 'dark';
  primaryColor?: string;
  [key: string]: unknown;
}

/**
 * BrandingTheme
 *
 * Controlled UL branding configuration.
 */
export type BrandingTheme = {
  mode?: 'light' | 'dark' | 'system';
  primaryColor?: string;
  borderRadius?: number;
  fontFamily?: string;
  [key: string]: unknown;
};

/**
 * CustomerOverrides
 *
 * Custom CSS variable overrides (e.g. "--button-radius": "6px").
 */
export type CustomOverrides = Record<string, string>;

/**
 * ThemeInput
 *
 * Optional props passed into the ThemeProvider.
 */
export type ThemeInput = {
  branding?: BrandingTheme;
  customOverrides?: CustomOverrides;
};

/**
 * ThemeContextValue
 *
 * The values made available through the ThemeContext.
 */
export type ThemeContextValue = {
  branding: BrandingTheme;
  customOverrides: CustomOverrides;
  mergedTheme: Record<string, unknown>;
};

import { cva, VariantProps } from 'class-variance-authority';
import * as React from 'react';
import { cn } from '@/lib/theme-utils';

const textFieldVariants = cva(
  "bg-input aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive theme-default:active:scale-[0.99] relative box-border inline-flex w-full shrink-0 cursor-text items-center justify-center gap-2 overflow-hidden rounded-2xl text-sm transition-[color,box-shadow] duration-150 ease-in-out outline-none disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4",
  {
    variants: {
      variant: {
        default:
          'border-border/50 text-input-foreground shadow-input-resting hover:shadow-input-hover hover:border-primary/25 focus-within:border-border focus-within:ring-primary/15 focus-within:ring-4',
        error:
          'border-destructive-border/50 text-destructive-foreground shadow-input-destructive-resting hover:shadow-input-destructive-hover hover:border-destructive-border/25 focus-within:ring-destructive-border/15 focus-within:ring-4',
      },
      size: {
        default: 'h-10',
        sm: 'h-9',
        lg: 'h-11',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  },
);

export interface TextFieldProps extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'size'> {
  error?: boolean;
  helperText?: string;
  size?: VariantProps<typeof textFieldVariants>['size'];
  variant?: VariantProps<typeof textFieldVariants>['variant'];
  startAdornment?: React.ReactNode;
  endAdornment?: React.ReactNode;
}

const TextField = React.forwardRef<HTMLInputElement, TextFieldProps>(
  (
    { className, variant, size, error, helperText, startAdornment, endAdornment, ...props },
    ref,
  ) => {
    const isDisabled = props.disabled;

    const WrapperComponent = props.id ? 'div' : 'label';

    return (
      <div className="flex w-full flex-col">
        <WrapperComponent
          className={cn(
            textFieldVariants({ variant: error ? 'error' : variant, size }),
            'group items-center gap-0.5',
            isDisabled &&
              'bg-input-muted text-input-muted-foreground cursor-not-allowed opacity-50',
            isDisabled && variant === 'default' && 'bg-input-muted',
            startAdornment && 'pl-[5px]',
            endAdornment && 'pr-[5px]',
            className,
          )}
        >
          {startAdornment && (
            <div className="[&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4">
              {startAdornment}
            </div>
          )}
          <input
            className={cn(
              'w-full flex-1 bg-transparent px-3 py-2 outline-none file:border-0 file:bg-transparent file:text-sm file:font-medium',
              isDisabled &&
                'bg-input-muted text-input-muted-foreground cursor-not-allowed opacity-50',
              startAdornment && 'pl-0',
              endAdornment && 'pr-0',
              size === 'default' && 'h-10',
              size === 'sm' && 'h-9',
              size === 'lg' && 'h-11',
            )}
            ref={ref}
            {...props}
          />
          {endAdornment && (
            <div className="[&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4">
              {endAdornment}
            </div>
          )}
        </WrapperComponent>
        {helperText && (
          <p
            className={cn(
              'mt-1.5 px-2 text-xs',
              error ? 'text-destructive-foreground' : 'text-muted-foreground',
            )}
          >
            {helperText}
          </p>
        )}
      </div>
    );
  },
);

TextField.displayName = 'TextField';

export { TextField, textFieldVariants };

'use client';

import { TextField } from '@/components/ui/text-field';
import React, { ClipboardEvent, KeyboardEvent, useRef, useState } from 'react';
import { cn } from '@/lib/theme-utils';

export interface OTPFieldProps {
  length?: number;
  className?: string;
  placeholder?: string;
  disabled?: boolean;
  onChange?: (value: string) => void;
  autoSubmit?: (value: string) => void;
  separator?: {
    character?: string;
    afterEvery?: number;
  };
}

function OTPField({
  length = 6,
  placeholder,
  disabled,
  className,
  onChange,
  autoSubmit,
  separator,
}: OTPFieldProps) {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<(HTMLInputElement | null)[]>([]);

  const handleChange = (element: HTMLInputElement, index: number) => {
    const value = element.value;
    if (isNaN(Number(value))) return;

    const newOtp = [...otp];
    newOtp[index] = value.substring(value.length - 1);
    setOtp(newOtp);

    const otpValue = newOtp.join('');
    onChange?.(otpValue);

    if (otpValue.length === length && autoSubmit) {
      autoSubmit(otpValue);
    }

    if (value && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>, index: number) => {
    if ((e.key === 'Delete' || e.key === 'Backspace') && (e.ctrlKey || e.metaKey)) {
      e.preventDefault();
      const newOtp = new Array(length).fill('');
      setOtp(newOtp);
      onChange?.('');
      inputRefs.current[0]?.focus();
      return;
    }

    if (e.key === 'ArrowLeft') {
      e.preventDefault();
      if (index > 0) {
        inputRefs.current[index - 1]?.focus();
      }
      return;
    }

    if (e.key === 'ArrowRight') {
      e.preventDefault();
      if (index < length - 1) {
        inputRefs.current[index + 1]?.focus();
      }
      return;
    }

    if (e.key === 'Home') {
      e.preventDefault();
      inputRefs.current[0]?.focus();
      return;
    }

    if (e.key === 'End') {
      e.preventDefault();
      inputRefs.current[length - 1]?.focus();
      return;
    }

    if (e.key === 'Backspace') {
      if (!otp[index] && index > 0) {
        const newOtp = [...otp];
        newOtp[index - 1] = '';
        setOtp(newOtp);
        onChange?.(newOtp.join(''));
        inputRefs.current[index - 1]?.focus();
      }
      return;
    }

    if (e.key === 'Delete') {
      if (otp[index]) {
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
        onChange?.(newOtp.join(''));
      } else if (index < length - 1) {
        const newOtp = [...otp];
        newOtp[index + 1] = '';
        setOtp(newOtp);
        onChange?.(newOtp.join(''));
      }
      return;
    }

    if (e.key === ' ') {
      e.preventDefault();
      const nextEmptyIndex = otp.findIndex((digit, i) => i > index && !digit);
      if (nextEmptyIndex !== -1) {
        inputRefs.current[nextEmptyIndex]?.focus();
      } else if (index < length - 1) {
        inputRefs.current[index + 1]?.focus();
      }
      return;
    }
  };

  const handlePaste = (e: ClipboardEvent<HTMLInputElement>, startIndex: number) => {
    e.preventDefault();
    const pasteData = e.clipboardData.getData('text').replace(/[^0-9]/g, '');

    if (!pasteData) return;

    const newOtp = [...otp];
    const availableSlots = length - startIndex;
    const digitsToPaste = pasteData.slice(0, availableSlots);

    for (let i = startIndex; i < Math.min(startIndex + digitsToPaste.length, length); i++) {
      newOtp[i] = '';
    }

    for (let i = 0; i < digitsToPaste.length && startIndex + i < length; i++) {
      newOtp[startIndex + i] = digitsToPaste[i];
    }

    setOtp(newOtp);
    const otpValue = newOtp.join('');
    onChange?.(otpValue);

    if (otpValue.length === length && autoSubmit) {
      autoSubmit(otpValue);
    }

    const nextFocusIndex = Math.min(startIndex + digitsToPaste.length, length - 1);
    setTimeout(() => {
      inputRefs.current[nextFocusIndex]?.focus();
    }, 0);
  };

  return (
    <div className={cn('flex w-full items-center gap-2', className)}>
      {otp.map((digit, index) => (
        <React.Fragment key={index}>
          <TextField
            ref={(el) => {
              inputRefs.current[index] = el as HTMLInputElement;
            }}
            type="text"
            inputMode="numeric"
            maxLength={1}
            value={digit}
            placeholder={placeholder}
            disabled={disabled}
            className="flex-1 text-xl font-semibold *:text-center has-[input]:text-center"
            onChange={(e) => handleChange(e.target, index)}
            onKeyDown={(e) => handleKeyDown(e, index)}
            onPaste={(e) => handlePaste(e, index)}
          />
          {separator?.afterEvery &&
            (index + 1) % separator.afterEvery === 0 &&
            index < length - 1 && (
              <span className="text-muted-foreground text-2xl font-semibold">
                {separator.character || '-'}
              </span>
            )}
        </React.Fragment>
      ))}
    </div>
  );
}

export { OTPField };

import { cva, VariantProps } from 'class-variance-authority';
import * as React from 'react';
import { cn } from '@/lib/theme-utils';

const spinnerVariants = cva(
  'text-surface inline-block h-8 w-8 rounded-full duration-[5000] ease-linear',
  {
    variants: {
      variant: {
        dots: 'border-primary animate-[spin_5s_linear_infinite] border-6 border-dotted',
        pulse: 'bg-primary animate-pulse',
        solid: 'border-primary animate-spin border-2 border-e-transparent',
      },
      size: {
        sm: 'size-4',
        md: 'size-8',
        lg: 'size-12',
      },
    },
    defaultVariants: {
      variant: 'solid',
      size: 'md',
    },
  },
);

export interface SpinnerProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof spinnerVariants> {}

export function Spinner({ variant, size, className, ...props }: SpinnerProps) {
  return (
    <div className={cn(spinnerVariants({ variant, size }), className)} {...props}>
      <span className="sr-only">Loading...</span>
    </div>
  );
}
